import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/forgot.css'; // Using the same styling as forgot password

function ResetPassword() {
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');
  const location = useLocation();
  const navigate = useNavigate();
  const [token, setToken] = useState(null);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const extractedToken = params.get('token');
    if (extractedToken) {
      setToken(extractedToken);
    } else {
      setMessage('Token de redefinição de senha não encontrado.');
    }
  }, [location.search]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!token) {
      setMessage('Token de redefinição de senha inválido ou ausente.');
      return;
    }

    try {
      const res = await fetch('http://localhost:3000/reset-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ token, novaSenha: newPassword })
      });

      const data = await res.json();
      setMessage(data.message || data.error);
      if (res.ok) {
        alert('Senha redefinida com sucesso!');
        navigate('/login'); // Redirect to login/sign-in page
      }
    } catch (err) {
      console.error(err);
      setMessage('Erro ao redefinir senha. Tente novamente mais tarde.');
    }
  };

  return (
    <div className="container">
      <h1>Redefinir senha</h1>
      <p>Crie uma nova senha forte para sua conta.</p>
      <form onSubmit={handleSubmit}>
        <input
          type="password"
          placeholder="Nova senha"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          required
        />
        <button type="submit">Redefinir</button>
      </form>
      <p>{message}</p>
    </div>
  );
}

export default ResetPassword;
