import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

/**
 * Componente do formulário de login.
 * Permite que os usuários existentes entrem na aplicação.
 */
function SignInForm() {
  const [email, setEmail] = useState('');
  // CORREÇÃO: Padronizando o nome do estado e da função que o atualiza
  const [senha, setSenha] = useState('');
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setMessageType('');

    // A validação já usa 'senha', o que está correto.
    if (!email || !senha) {
      setMessage('Por favor, preencha todos os campos!');
      setMessageType('error');
      return;
    }

    try {
      const response = await fetch('/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        // O corpo da requisição já usa 'senha', o que está correto.
        body: JSON.stringify({ email, senha })
      });
      const data = await response.json();

      if (response.ok) {
        setMessage('Login bem-sucedido!');
        setMessageType('success');
        localStorage.setItem('user', JSON.stringify(data.user));
        navigate('/home');
      } else {
        setMessage(data.error || 'Credenciais inválidas.');
        setMessageType('error');
      }
    } catch (err) {
      console.error("Erro na requisição:", err);
      setMessage('Erro ao conectar ao servidor. Tente novamente.');
      setMessageType('error');
    }
  };

  return (
    <div className="form-container sign-in-container">
      <form onSubmit={handleSubmit}>
        <h1>Entrar</h1>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Senha"
          // CORREÇÃO: O 'value' já estava certo, usando 'senha'.
          value={senha}
          // CORREÇÃO: A função chamada agora é 'setSenha'.
          onChange={(e) => setSenha(e.target.value)}
          required
        />
        <button type="submit">Entrar</button>
        <p><Link to="/forgot-password">Esqueceu a senha?</Link></p>
        {message && (
          <p className={`message-box ${messageType === 'success' ? 'message-success' : 'message-error'}`}>
            {message}
          </p>
        )}
      </form>
    </div>
  );
}

export default SignInForm;
