import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

/**
 * Componente do formulário de login.
 * Permite que os usuários existentes entrem na aplicação.
 */
function SignInForm() {
  const [email, setEmail] = useState('');
  const [senha, setPassword] = useState('');
  const [message, setMessage] = useState(''); // Estado para mensagens de feedback
  const [messageType, setMessageType] = useState(''); // 'success' ou 'error'
  const navigate = useNavigate();

  /**
   * Lida com o envio do formulário de login.
   * @param {Event} e - O evento de envio do formulário.
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(''); // Limpa mensagens anteriores
    setMessageType('');

    if (!email || !password) {
      setMessage('Por favor, preencha todos os campos!');
      setMessageType('error');
      return;
    }

    try {
      const response = await fetch('/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, senha })
      });
      const data = await response.json();

      if (response.ok) {
        setMessage('Login bem-sucedido!');
        setMessageType('success');
        localStorage.setItem('user', JSON.stringify(data.user)); // Armazena os dados do usuário
        navigate('/home'); // Redireciona para a página inicial
      } else {
        setMessage(data.error || 'Credenciais inválidas.');
        setMessageType('error');
      }
    } catch (err) {
      console.error("Erro na requisição:", err);
      setMessage('Erro ao conectar ao servidor. Tente novamente.');
      setMessageType('error');
    }
  };

  return (
    <div className="form-container sign-in-container">
      <form onSubmit={handleSubmit}>
        <h1>Entrar</h1>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Entrar</button>
        {/* Link para a página de esqueci a senha */}
        <p><Link to="/forgot-password">Esqueceu a senha?</Link></p>
        {/* Exibe a mensagem de feedback se houver uma */}
        {message && (
          <p className={`message-box ${messageType === 'success' ? 'message-success' : 'message-error'}`}>
            {message}
          </p>
        )}
      </form>
    </div>
  );
}

export default SignInForm;
