import React, { useState } from 'react';

function SignUpForm({ onSignUpSuccess, onTogglePanel }) {
  const [currentStep, setCurrentStep] = useState(0);
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    senha: '',
    cargo: '',
    cpf: '',
    telefone: '',
    nascimento: '',
    rua: '',
    numero: '',
    cidade: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const formatarTelefone = (value) => {
    let v = value.replace(/\D/g, '');
    if (v.length > 11) v = v.slice(0, 11);
    v = v.replace(/(\d{2})(\d)/, '($1) $2');
    v = v.replace(/(\d{5})(\d)/, '$1-$2');
    return v;
  };

  const formatarCPF = (value) => {
    let v = value.replace(/\D/g, '');
    if (v.length > 11) v = v.slice(0, 11);
    v = v.replace(/(\d{3})(\d)/, '$1.$2');
    v = v.replace(/(\d{3})(\d)/, '$1.$2');
    v = v.replace(/(\d{3})(\d{2})$/, '$1-$2');
    return v;
  };

  const nextStep = () => {
    setCurrentStep(prev => prev + 1);
  };

  const prevStep = () => {
    setCurrentStep(prev => prev - 1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validação mínima dos campos de Funcionario
    if (
      !formData.nome ||
      !formData.cargo ||
      !formData.telefone ||
      !formData.cpf ||
      !formData.nascimento ||
      !formData.rua ||
      !formData.numero ||
      !formData.cidade
    ) {
      alert('Preencha todos os campos de funcionário!');
      return;
    }

    // Validação dos campos de Administrador
    if (!formData.email || !formData.senha) {
      alert('Preencha email e senha para o administrador!');
      return;
    }

    try {
      // 1º passo: cadastrar o funcionário
      const resFunc = await fetch('/funcionario', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          nome: formData.nome,
          cargo: formData.cargo,
          telefone: formData.telefone,
          data_nascimento: formData.nascimento,
          rua: formData.rua,
          numero: formData.numero,
          cidade: formData.cidade,
          cpf: formData.cpf
        })
      });

      if (!resFunc.ok) {
        const err = await resFunc.json();
        alert(err.error || 'Erro ao cadastrar funcionário');
        return;
      }

      const jsonFunc = await resFunc.json();
      const funcionarioId = jsonFunc.id;

      // 2º passo: cadastrar o administrador, informando funcionario_codigo
      const resAdm = await fetch('http://localhost:3000/administrador', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: formData.email,
          senha: formData.senha,
          funcionario_codigo: funcionarioId
        })
      });

      if (!resAdm.ok) {
        const errAdm = await resAdm.json();
        alert(errAdm.error || 'Erro ao cadastrar administrador');
        return;
      }

      alert('Cadastro realizado com sucesso!');
      setFormData({ // Reset form
        nome: '', email: '', senha: '', cargo: '', cpf: '',
        telefone: '', nascimento: '', rua: '', numero: '', cidade: ''
      });
      setCurrentStep(0);
      onSignUpSuccess(); // Callback to parent to switch back to sign-in
    } catch (error) {
      console.error(error);
      alert('Erro ao conectar ao servidor');
    }
  };

  return (
    <div className="form-container sign-up-container">
      <h1>Criar conta</h1>
      <form id="multiStepForm" onSubmit={handleSubmit}>
        {/* Etapa 1 */}
        <div className={`step ${currentStep === 0 ? 'active' : ''}`}>
          <input
            type="text"
            name="nome"
            placeholder="Nome completo"
            value={formData.nome}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="senha"
            placeholder="Senha"
            value={formData.senha}
            onChange={handleChange}
            required
          />
          <button type="button" onClick={nextStep}>Próximo</button>
        </div>

        {/* Etapa 2 */}
        <div className={`step ${currentStep === 1 ? 'active' : ''}`}>
          <input
            type="text"
            name="cargo"
            placeholder="Cargo"
            value={formData.cargo}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="cpf"
            placeholder="CPF"
            maxLength="14"
            value={formatarCPF(formData.cpf)}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="telefone"
            placeholder="Telefone"
            maxLength="15"
            value={formatarTelefone(formData.telefone)}
            onChange={handleChange}
            required
          />
          <input
            type="date"
            name="nascimento"
            placeholder="Data de nascimento"
            value={formData.nascimento}
            onChange={handleChange}
            required
          />
          <button type="button" onClick={prevStep}>Voltar</button>
          <button type="button" onClick={nextStep}>Próximo</button>
        </div>

        {/* Etapa 3 */}
        <div className={`step ${currentStep === 2 ? 'active' : ''}`}>
          <input
            type="text"
            name="rua"
            placeholder="Rua"
            value={formData.rua}
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="numero"
            placeholder="Número"
            value={formData.numero}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="cidade"
            placeholder="Cidade"
            value={formData.cidade}
            onChange={handleChange}
            required
          />
          <button type="button" onClick={prevStep}>Voltar</button>
          <button type="submit">Criar Conta</button>
        </div>
      </form>
    </div>
  );
}

export default SignUpForm;
