import React, { useState } from 'react';
import '../styles/forgot.css'; // Import the CSS

function ForgotPassword() {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("Enviando e-mail...");

    try {
      const res = await fetch('http://localhost:3000/forgot-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email })
      });

      const data = await res.json();
      setMessage(data.message || data.error);
    } catch (error) {
      setMessage("Erro ao enviar e-mail. Tente novamente.");
      console.error(error);
    }
  };

  return (
    <div className="container">
      <h1>Esqueceu sua senha?</h1>
      <p>Informe seu e-mail cadastrado no site para enviarmos as instruções de redefinição da senha.</p>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Seu e-mail"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <button type="submit">Continuar</button>
      </form>
      <p id="mensagem">{message}</p>
    </div>
  );
}

export default ForgotPassword;
