import React, { useState } from 'react';
import SignInForm from './SignInForm';
import SignUpForm from './SignUpForm';
import '../styles/login.css'; // Import the CSS
import Logo from '../assets/Logo.png';

function AuthPage() {
  const [isRightPanelActive, setIsRightPanelActive] = useState(false);

  const handleSignUpClick = () => {
    setIsRightPanelActive(true);
  };

  const handleSignInClick = () => {
    setIsRightPanelActive(false);
  };

  const handleSignUpSuccess = () => {
    setIsRightPanelActive(false); // Switch to sign-in panel after successful sign-up
  };

  return (
    <div className={`container ${isRightPanelActive ? 'right-panel-active' : ''}`} id="container" style={{outline: '2px solid red' }}>
      <SignInForm />
      <SignUpForm onSignUpSuccess={handleSignUpSuccess} />

      <div className="overlay-container">
        <div className="overlay">
          <div className="overlay-panel overlay-left">
            <h2>Bem-vindo de volta à</h2>
            <h1>Manejo GR!</h1>
            <p>Já possui uma conta?</p>
            <button id="sign-in" onClick={handleSignInClick}>Entrar</button>
            {/* Make sure the image path is correct relative to your public folder or src */}
            <img src={Logo} alt="Logo Manejo" width="110px" />
          </div>
          <div className="overlay-panel overlay-right">
            <h2>Bem-vindo à</h2>
            <h1>Manejo GR!</h1>
            <p>Novo por aqui?</p>
            <button id="sign-up" onClick={handleSignUpClick}>Criar Conta</button>
            <img src={Logo} alt="Logo Manejo" width="110px" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default AuthPage;
